import React from 'react';
import './css/Services.css';

const servicesData = [
  {
    title: "Web Development",
    content: "Our web development services are tailored to meet your business needs. We specialize in creating responsive and dynamic websites using the latest web technologies. Whether you need a simple website or a complex web application, our team has the expertise to bring your vision to life. We focus on user-centric design, ensuring that your website not only looks great but also delivers an exceptional user experience."
  },
  {
    title: "Front-End Solutions",
    content: "In today's web-centric landscape, a captivating and efficient front end is essential for engaging your audience. Our web front-end services are designed to elevate your online presence, providing a seamless and visually appealing user experience. Catering to diverse needs, we specialize in crafting feature-rich and intuitive web applications."
  },
  {
    title: "UI/UX Design",
    content: "Great design is at the heart of everything we do. Our design team crafts user-friendly interfaces and visually appealing designs for websites and applications. We understand the importance of creating designs that not only look good but also enhance the user experience. From wireframing to prototyping, we pay attention to every detail to create designs that engage and delight your users."
  },
  {
    title: "Inbound Marketing",
    content: "In addition to our front-end and design services, we also offer inbound marketing solutions to help your business thrive in the digital landscape. By leveraging inbound marketing strategies such as content creation, SEO optimization, email marketing, and social media management, we can attract, engage, and delight your target audience. With our inbound marketing approach, we ensure that your brand not only gets noticed but also builds meaningful connections with potential customers, leading to increased traffic, engagement, and ultimately, conversions."
  },
  // ... Add more services as needed
];

const Service = ({ title, content }) => (
  <section>
    <h2>{title}</h2>
    <p>{content}</p>
    <br />
  </section>
);

const Services = () => {
  return (
    <div className="container">
      <header>
        <h1>Our Services - Soft<span className="blood">D</span>rakula</h1>
      </header>
      <main>
        {servicesData.map((service, index) => (
          <Service key={index} title={service.title} content={service.content} />
        ))}
      </main>
      <footer>
        <p>&copy; 2024 Proudly powered by Soft<span className="blood">D</span>rakula</p>
        <button id="backButton" onClick={() => window.history.back()}>Back</button>
      </footer>
    </div>
  );
};

export default Services;
