import React from 'react';
import './Products.css'; // Import the CSS file

class Products extends React.Component {
    redirectToSpa = () => {
        window.location.href = 'Spa.html';
    }

    redirectToMotorcycleParts = () => {
        window.location.href = 'motorcycleParts.html';
    }

    redirectToClock = () => {
        window.location.href = 'clock.html';
    }

    render() {
        return (
            <div className="container">
                <header>
                    <h1>Our Portfolio</h1>
                </header>
                <main>
                    <div className="content text-container">
                        <legend className="text">Welcome to Soft<span style={{ color: '#ff6600' }}>D</span>rakula</legend>
                        <p>
                            Soft<span style={{ color: '#ff6600' }}>D</span>rakula specializes in transforming ideas into stunning digital experiences. From user-friendly websites and captivating blogs to robust e-commerce platforms and innovative applications, we've got you covered. Our toolbox includes plugins, calculators, digital clocks, financial control tools, APIs, web page translators, and theme-changing systems, all designed to enhance your digital space. With a focus on high-quality code and design, each project receives individual attention to ensure it exceeds your expectations.
                        </p>
                        <p>Partner with Soft<span style={{ color: '#ff6600' }}>D</span>rakula for a holistic approach to digital transformation</p>
                    </div>
                    <div className="content">
                        <h2>Crafting front-end, and UX/UI design solutions</h2>
                        <p>Check out some of our projects below</p>
                        <div className="button-container">
                            <button className="segment-button" onClick={this.redirectToSpa}>Kaasboerderij</button>
                            <button className="segment-button" onClick={this.redirectToMotorcycleParts}>MotorcycleParts</button>
                            <button className="segment-button" onClick={this.redirectToClock}>Clock</button>
                        </div>
                    </div>
                </main>
                <footer>
                    <p>&copy; 2024 Proudly powered by Soft<span style={{ color: '#ff6600' }}>D</span>rakula</p>
                    <button id="backButton">Back</button>
                </footer>
            </div>
        );
    }
}

export default Products;
